import java.io.ObjectInputStream
import java.io.ObjectOutputStream
import java.io.IOException
import java.io.Serializable
import org.apache.datasketches.theta.Sketch
import org.apache.datasketches.theta.UpdateSketch
import org.apache.datasketches.theta.CompactSketch
import org.apache.datasketches.theta.Sketches
import org.apache.datasketches.memory.Memory


class ThetaSketchJavaSerializable() extends Serializable {
  private var sketch:Sketch = null;
  def this(sketch: Sketch) {
    this()
    this.sketch = sketch
  }

  def getSketch: Sketch = sketch

  def getCompactSketch: CompactSketch = {
    if (sketch == null) return null
    if (sketch.isInstanceOf[UpdateSketch]) return sketch.asInstanceOf[UpdateSketch].compact
    sketch.asInstanceOf[CompactSketch]
  }

  def update(value: String): Unit = {
    if (sketch == null) sketch = UpdateSketch.builder.build
    if (sketch.isInstanceOf[UpdateSketch]) sketch.asInstanceOf[UpdateSketch].update(value)
    else throw new RuntimeException("update() on read-only sketch")
  }

  def getEstimate: Double = {
    if (sketch == null) return 0.0
    sketch.getEstimate
  }

  @throws[IOException]
  private def writeObject(out: ObjectOutputStream): Unit = {
    if (sketch == null) {
      out.writeInt(0)
      return
    }
    val serializedSketchBytes = sketch.asInstanceOf[UpdateSketch].compact().toByteArray
    out.writeInt(serializedSketchBytes.length)
    out.write(serializedSketchBytes)
  }

  @throws[IOException]
  @throws[ClassNotFoundException]
  private def readObject(in: ObjectInputStream): Unit = {
    val length = in.readInt
    if (length == 0) return
    val serializedSketchBytes = new Array[Byte](length)
    in.readFully(serializedSketchBytes)
    sketch = Sketches.wrapSketch(Memory.wrap(serializedSketchBytes))
  }

