def computeSketch(data: DataFrame) = {
    val mappedData = data.mapPartitions(new MapPartitionsFunction[Row, ThetaSketchJavaSerializable]() {
      override def call(it: util.Iterator[Row]): util.Iterator[ThetaSketchJavaSerializable] = {
        val sketch = new ThetaSketchJavaSerializable
        while ( {
          it.hasNext
        }) sketch.update(it.next.get(0).asInstanceOf[String])
        util.Arrays.asList(sketch).iterator
      }
    }, Encoders.javaSerialization(classOf[ThetaSketchJavaSerializable]))
    val sketch = mappedData.reduce(new ReduceFunction[ThetaSketchJavaSerializable]() {
      @throws[Exception]
      override def call(sketch1: ThetaSketchJavaSerializable, sketch2: ThetaSketchJavaSerializable): ThetaSketchJavaSerializable = {
        if (sketch1.getSketch == null && sketch2.getSketch == null) return new ThetaSketchJavaSerializable(UpdateSketch.builder.build.compact)
        if (sketch1.getSketch == null) return sketch2
        if (sketch2.getSketch == null) return sketch1
        val compactSketch1 = sketch1.getCompactSketch
        val compactSketch2 = sketch2.getCompactSketch
        new ThetaSketchJavaSerializable(PairwiseSetOperations.union(compactSketch1, compactSketch2))
      }
    })
    sketch

